import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';   // ✅ ADD THIS
import { useAuth } from '../utils/auth';

export default function ProfilePage(){
  const { id } = useParams();
  const { api, user } = useAuth();
  const [profile, setProfile] = useState(null);
  useEffect(()=>{ if(id) load(); },[id]);
    const nav = useNavigate();

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    alert('Logged out successfully');
    nav('/login');
  };

  const load = async ()=>{
    try{ const res = await api.get('/users/'+id); setProfile(res.data.user); }catch(err){ console.error(err); }
  };
  if(!profile) return <div className='app'><div className='card'>Loading...</div></div>
  return (
    <div className='app'>
      <div className='card' style={{display:'flex',gap:16,alignItems:'center'}}>
        <img src={'http://localhost:5000'+profile.profilePic} alt='avatar' style={{width:96,height:96,borderRadius:12,objectFit:'cover'}}/>
        <div>
          <h2>{profile.username}</h2>
          <p className='muted'>{profile.bio}</p>
          <div className='muted'>{profile.followers?.length || 0} followers • {profile.following?.length || 0} following</div>
        </div>
      </div>
      <h3 style={{marginTop:12}}>Posts</h3>
      <div className='grid'>
        {profile.posts?.map(p=> (
          <div key={p._id} className='card post-card'>
            <img src={'http://localhost:5000'+p.imagePath} alt='post' />
          </div>
        ))}
      </div>
    </div>
  )
}
