import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../utils/auth";
import axios from "axios";

export default function PostPage() {
  const { id } = useParams();
  const { api } = useAuth();

  const [post, setPost] = useState(null);
  const [comment, setComment] = useState("");

  const [authorProfile, setAuthorProfile] = useState(null);

  // null = not initialized yet; true/false = explicit state
  const [amIFollowing, setAmIFollowing] = useState(null);

  const nav = useNavigate();
  const token = localStorage.getItem("token");

  const getMeId = () => {
    try {
      return JSON.parse(localStorage.getItem("user"))._id;
    } catch {
      return null;
    }
  };

  // ---------- load post + author (does NOT overwrite follow state) ----------
  const load = async () => {
    try {
      const res = await api.get("/posts/" + id);
      setPost(res.data.post);

      if (res.data.post?.author?._id) {
        const userRes = await api.get("/users/" + res.data.post.author._id);
        setAuthorProfile(userRes.data.user);

        // Initialize follow state only if not set yet
        const meId = getMeId();
        if (amIFollowing === null && meId) {
          const isFollowing = !!userRes.data.user.followers?.some(
            (f) => String(f) === String(meId)
          );
          setAmIFollowing(isFollowing);
        }
      }
    } catch (err) {
      console.error("load error:", err);
    }
  };

  useEffect(() => {
    if (id) load();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  // ---------- toggle follow/unfollow ----------
  const toggleFollow = async () => {
    try {
      const meId = getMeId();
      if (!meId || !post) return;

      // optimistic UI toggle
      setAmIFollowing((prev) => {
        // if prev is null (not initialized) treat as false
        const next = !prev;
        // adjust authorProfile followers optimistically
        setAuthorProfile((prevAuthor) => {
          if (!prevAuthor) return prevAuthor;
          const prevFollowers = prevAuthor.followers || [];
          if (next) {
            // add me if not present
            if (!prevFollowers.some((f) => String(f) === String(meId))) {
              return { ...prevAuthor, followers: [...prevFollowers, meId] };
            }
            return prevAuthor;
          } else {
            // remove me
            return {
              ...prevAuthor,
              followers: prevFollowers.filter((f) => String(f) !== String(meId)),
            };
          }
        });
        return next;
      });

      // call backend
      const resp = await axios.post(
        `http://localhost:5000/api/users/${post.author._id}/follow`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // backend gives final state: { following: true/false, followersCount: N } (your route)
      const finalFollowing = resp.data.following;
      setAmIFollowing(finalFollowing);

      // ensure authorProfile followers array matches finalFollowing:
      setAuthorProfile((prevAuthor) => {
        if (!prevAuthor) return prevAuthor;
        const prevFollowers = prevAuthor.followers || [];
        if (finalFollowing) {
          // add if missing
          if (!prevFollowers.some((f) => String(f) === String(meId))) {
            return { ...prevAuthor, followers: [...prevFollowers, meId] };
          }
          return prevAuthor;
        } else {
          // remove me
          return {
            ...prevAuthor,
            followers: prevFollowers.filter((f) => String(f) !== String(meId)),
          };
        }
      });
    } catch (err) {
      console.error("toggleFollow error:", err);
      // revert optimistic UI on error (keep safe)
      // it's safer to re-fetch author but we avoid re-fetch to not clobber state:
      // revert local amIFollowing to opposite
      setAmIFollowing((prev) => (prev === null ? false : !prev));
    }
  };

  // ---------- like / unlike (update local post state from API response) ----------
  const like = async () => {
    try {
      const meId = getMeId();
      const res = await api.post("/posts/" + id + "/like");
      // posts route responds: { liked: true/false, likes: count } per our backend
      const liked = res.data.liked;
      // update local post.likes array based on liked flag
      setPost((prev) => {
        if (!prev) return prev;
        const prevLikes = prev.likes || [];
        if (liked) {
          // add meId if not present
          if (!prevLikes.some((u) => String(u) === String(meId))) {
            return { ...prev, likes: [...prevLikes, meId] };
          }
          return { ...prev, likes: prevLikes };
        } else {
          // remove meId
          return {
            ...prev,
            likes: prevLikes.filter((u) => String(u) !== String(meId)),
          };
        }
      });
    } catch (err) {
      console.error("like error:", err);
    }
  };

  // ---------- save/unsave (optimistic local updates) ----------
  const save = async () => {
    try {
      const meId = getMeId();
      const res = await api.post("/posts/" + id + "/save");
      // res.data.saved = true/false
      // We don't store saved status in this page, so we won't call load() to avoid clobbering follow state.
      // If you want to reflect saved status elsewhere, handle it in parent/global state.
      return res.data;
    } catch (err) {
      console.error("save error:", err);
    }
  };

  // ---------- add comment (use response comments to update local post) ----------
  const addComment = async () => {
    try {
      const res = await api.post("/posts/" + id + "/comment", { text: comment });
      // res.data.comments = updated comments array
      setComment("");
      setPost((prev) => (prev ? { ...prev, comments: res.data.comments } : prev));
    } catch (err) {
      console.error("comment error:", err);
    }
  };

  // ---------- rate (use returned avgRating) ----------
  const rate = async (s) => {
    try {
      const res = await api.post("/posts/" + id + "/rate", { score: s });
      setPost((prev) => (prev ? { ...prev, avgRating: res.data.avgRating } : prev));
    } catch (err) {
      console.error("rate error:", err);
    }
  };

  if (!post)
    return (
      <div className="app">
        <div className="card">Loading...</div>
      </div>
    );

  const meId = getMeId();

  return (
    <div className="app">
      <div className="card" style={{ display: "flex", gap: 16 }}>
        <img src={`http://localhost:5000${post.imagePath}`} alt="post" />

        <div style={{ flex: 1 }}>
          <h3
            style={{ cursor: "pointer" }}
            onClick={() => nav("/profile/" + post.author._id)}
          >
            {post.author.username}
          </h3>

          {/* FOLLOWERS • FOLLOWING */}
          {authorProfile && (
            <div className="muted" style={{ marginBottom: 6 }}>
              {authorProfile.followers?.length || 0} followers •{" "}
              {authorProfile.following?.length || 0} following
            </div>
          )}

          {/* FOLLOW / FOLLOWING BUTTON */}
          {authorProfile && authorProfile._id !== meId && amIFollowing !== null && (
            <button
              className="btn"
              onClick={toggleFollow}
              style={{
                background: amIFollowing ? "#5a275a" : "#8b005d",
                color: "white",
                padding: "5px 14px",
                fontSize: 14,
                borderRadius: 8,
                marginBottom: 12,
                transition: "0.15s ease",
              }}
            >
              {amIFollowing ? "Following" : "Follow"}
            </button>
          )}

          <p className="muted">{post.caption}</p>

          <div style={{ marginTop: 12 }}>
            <button className="btn" onClick={like}>
              <i className="fa fa-heart"></i>{" "}
              {Array.isArray(post.likes) ? post.likes.length : post.likes || 0}
            </button>
            <button className="btn" onClick={save} style={{ marginLeft: 8 }}>
              <i className="fa fa-bookmark"></i>
            </button>
          </div>

          <div style={{ marginTop: 12 }}>
            <strong>Average rating:</strong> {post.avgRating?.toFixed(1) || 0}
            <div style={{ marginTop: 8 }}>
              {[1, 2, 3, 4, 5].map((n) => (
                <button
                  key={n}
                  onClick={() => rate(n)}
                  className="btn"
                  style={{ marginRight: 6 }}
                >
                  {n}★
                </button>
              ))}
            </div>
          </div>

          <div style={{ marginTop: 12 }}>
            <h4>Comments</h4>
            {post.comments?.map((c) => (
              <div key={c._id} className="card" style={{ marginBottom: 8 }}>
                <strong>{c.author?.username}</strong>
                <p className="muted">{c.text}</p>
              </div>
            ))}

            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              rows={3}
            ></textarea>

            <button className="btn" onClick={addComment}>
              Add comment
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
