import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../utils/auth";
import axios from "axios";

export default function PostPage() {
  const { id } = useParams();
  const { api } = useAuth();

  const [post, setPost] = useState(null);
  const [comment, setComment] = useState("");

  const [authorProfile, setAuthorProfile] = useState(null);
  const [amIFollowing, setAmIFollowing] = useState(false);

  const nav = useNavigate();
  const token = localStorage.getItem("token");

  // LOAD POST + AUTHOR
  useEffect(() => {
    if (id) load();
  }, [id]);

  const load = async () => {
    try {
      const res = await api.get("/posts/" + id);
      setPost(res.data.post);

      if (res.data.post?.author?._id) {
        const userRes = await api.get("/users/" + res.data.post.author._id);
        setAuthorProfile(userRes.data.user);

        const me = JSON.parse(localStorage.getItem("user"));

        // ⭐ SET INITIAL FOLLOW STATE ONLY ONCE
        if (!post) {
          setAmIFollowing(userRes.data.user.followers?.includes(me?._id));
        }
      }
    } catch (err) {
      console.error(err);
    }
  };

  // ============================
  // ⭐ FIXED FOLLOW / UNFOLLOW
  // ============================
  const toggleFollow = async () => {
    try {
      const newState = !amIFollowing;
      setAmIFollowing(newState);

      const meId = JSON.parse(localStorage.getItem("user"))._id;

      setAuthorProfile((prev) => ({
        ...prev,
        followers: newState
          ? [...prev.followers, meId] // FOLLOW → add myself
          : prev.followers.filter((f) => f !== meId) // UNFOLLOW → remove myself
      }));

      await axios.post(
        `http://localhost:5000/api/users/${post.author._id}/follow`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
    } catch (err) {
      console.error(err);
    }
  };

  // ============================
  // LIKE, SAVE, COMMENT, RATE
  // ============================
  const like = async () => {
    try {
      await api.post("/posts/" + id + "/like");
      load();
    } catch (err) {
      console.error(err);
    }
  };

  const save = async () => {
    try {
      await api.post("/posts/" + id + "/save");
      load();
    } catch (err) {
      console.error(err);
    }
  };

  const addComment = async () => {
    try {
      await api.post("/posts/" + id + "/comment", { text: comment });
      setComment("");
      load();
    } catch (err) {
      console.error(err);
    }
  };

  const rate = async (s) => {
    try {
      await api.post("/posts/" + id + "/rate", { score: s });
      load();
    } catch (err) {
      console.error(err);
    }
  };

  if (!post)
    return (
      <div className="app">
        <div className="card">Loading...</div>
      </div>
    );

  const meId = JSON.parse(localStorage.getItem("user"))._id;

  return (
    <div className="app">
      <div className="card" style={{ display: "flex", gap: 16 }}>
        <img src={`http://localhost:5000${post.imagePath}`} alt="post" />

        <div style={{ flex: 1 }}>
          <h3
            style={{ cursor: "pointer" }}
            onClick={() => nav("/profile/" + post.author._id)}
          >
            {post.author.username}
          </h3>

          {/* FOLLOWERS • FOLLOWING */}
          {authorProfile && (
            <div className="muted" style={{ marginBottom: 6 }}>
              {authorProfile.followers?.length || 0} followers •{" "}
              {authorProfile.following?.length || 0} following
            </div>
          )}

          {/* FOLLOW BUTTON */}
          {authorProfile && authorProfile._id !== meId && (
            <button
              className="btn"
              onClick={toggleFollow}
              style={{
                background: amIFollowing ? "#5a275a" : "#8b005d",
                color: "white",
                padding: "5px 14px",
                fontSize: 14,
                borderRadius: 8,
                marginBottom: 12,
                transition: "0.15s ease",
              }}
            >
              {amIFollowing ? "Following" : "Follow"}
            </button>
          )}

          <p className="muted">{post.caption}</p>

          <div style={{ marginTop: 12 }}>
            <button className="btn" onClick={like}>
              <i className="fa fa-heart"></i> {post.likes?.length || 0}
            </button>
            <button className="btn" onClick={save} style={{ marginLeft: 8 }}>
              <i className="fa fa-bookmark"></i>
            </button>
          </div>

          <div style={{ marginTop: 12 }}>
            <strong>Average rating:</strong> {post.avgRating?.toFixed(1) || 0}
            <div style={{ marginTop: 8 }}>
              {[1, 2, 3, 4, 5].map((n) => (
                <button
                  key={n}
                  onClick={() => rate(n)}
                  className="btn"
                  style={{ marginRight: 6 }}
                >
                  {n}★
                </button>
              ))}
            </div>
          </div>

          <div style={{ marginTop: 12 }}>
            <h4>Comments</h4>
            {post.comments?.map((c) => (
              <div key={c._id} className="card" style={{ marginBottom: 8 }}>
                <strong>{c.author?.username}</strong>
                <p className="muted">{c.text}</p>
              </div>
            ))}

            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              rows={3}
            ></textarea>

            <button className="btn" onClick={addComment}>
              Add comment
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
