import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useAuth } from '../utils/auth';

export default function PostPage(){
  const { id } = useParams();
  const { api } = useAuth();
  const [post, setPost] = useState(null);
  const [comment, setComment] = useState('');
  useEffect(()=>{ if(id) load(); },[id]);
  const load = async ()=>{ try{ const res = await api.get('/posts/'+id); setPost(res.data.post); }catch(err){ console.error(err); } };

  const addComment = async ()=>{
    try{ await api.post('/posts/'+id+'/comment', { text: comment }); setComment(''); load(); }catch(err){ console.error(err); }
  }
  const rate = async (s)=>{ try{ await api.post('/posts/'+id+'/rate', { score:s }); load(); }catch(err){ console.error(err); } }
  const like = async ()=>{ try{ await api.post('/posts/'+id+'/like'); load(); }catch(err){ console.error(err); } }
  const save = async ()=>{ try{ await api.post('/posts/'+id+'/save'); load(); }catch(err){ console.error(err); } }

  if(!post) return <div className='app'><div className='card'>Loading...</div></div>
  return (
    <div className='app'>
      <div className='card' style={{display:'flex',gap:16}}>
        <img src={'http://localhost:5000'+post.imagePath} alt='post' style={{width:440, height:440, objectFit:'cover', borderRadius:12}}/>
        <div style={{flex:1}}>
          <h3>{post.author.username}</h3>
          <p className='muted'>{post.caption}</p>
          <div style={{marginTop:12}}>
            <button className='btn' onClick={like}><i className='fa fa-heart'></i> {post.likes?.length||0}</button>
            <button className='btn' onClick={save} style={{marginLeft:8}}><i className='fa fa-bookmark'></i></button>
          </div>
          <div style={{marginTop:12}}>
            <strong>Average rating:</strong> {post.avgRating?.toFixed(1) || 0}
            <div style={{marginTop:8}}>
              {[1,2,3,4,5].map(n=> <button key={n} onClick={()=>rate(n)} className='btn' style={{marginRight:6}}>{n}â˜…</button>)}
            </div>
          </div>
          <div style={{marginTop:12}}>
            <h4>Comments</h4>
            {post.comments?.map(c=> <div key={c._id} className='card' style={{marginBottom:8}}><strong>{c.author?.username}</strong><p className='muted'>{c.text}</p></div>)}
            <textarea value={comment} onChange={e=>setComment(e.target.value)} rows={3}></textarea>
            <button className='btn' onClick={addComment}>Add comment</button>
          </div>
        </div>
      </div>
    </div>
  )
}
