import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../utils/auth';
import axios from 'axios';

export default function ProfilePage() {
  const { id } = useParams();
  const { api, user } = useAuth();

  const [profile, setProfile] = useState(null);
  const [saved, setSaved] = useState([]);
  const [followers, setFollowers] = useState([]);
  const [following, setFollowing] = useState([]);

  const nav = useNavigate();
  const token = localStorage.getItem("token");

  // ---------------------------
  // LOGOUT
  // ---------------------------
  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    alert("Logged out successfully");
    nav("/login");
  };

  // ---------------------------
  // LOAD PROFILE DATA
  // ---------------------------
  const loadProfile = async () => {
    try {
      const res = await api.get("/users/" + id);
      setProfile(res.data.user);

      // update followers/following list
      setFollowers(res.data.user.followers || []);
      setFollowing(res.data.user.following || []);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    if (id) loadProfile();
  }, [id]);

  // ---------------------------
  // LOAD SAVED POSTS
  // ---------------------------
  useEffect(() => {
    const loadSaved = async () => {
      try {
        const res = await axios.get(
          "http://localhost:5000/api/users/me/saved",
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setSaved(res.data.saved || []);
      } catch (err) {
        console.error(err);
      }
    };

    loadSaved();
  }, []);

  // ---------------------------
  // FOLLOW / UNFOLLOW
  // ---------------------------
  const toggleFollow = async () => {
    try {
      const res = await axios.post(
        `http://localhost:5000/api/users/${id}/follow`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // success → refresh profile
      loadProfile();
    } catch (err) {
      console.error(err);
    }
  };

  // check if I already follow this user
  const amIFollowing = following.includes(user?._id);

  if (!profile)
    return (
      <div className="app">
        <div className="card">Loading...</div>
      </div>
    );

  return (
    <div className="app">

      {/* LOGOUT BUTTON */}
      <button
        onClick={logout}
        className="btn"
        style={{
          float: "right",
          background: "#8b005d",
          color: "white",
          marginBottom: "10px",
        }}
      >
        Logout
      </button>

      {/* PROFILE CARD */}
      <div
        className="card"
        style={{ display: "flex", gap: 16, alignItems: "center" }}
      >
        <img
          src={"http://localhost:5000" + profile.profilePic}
          alt="avatar"
          style={{
            width: 96,
            height: 96,
            borderRadius: 12,
            objectFit: "cover",
          }}
        />

        <div>
          <h2>{profile.username}</h2>
          <p className="muted">{profile.bio}</p>

          <div className="muted">
            {followers.length} followers • {following.length} following
          </div>

          {/* FOLLOW / UNFOLLOW BUTTON (only if viewing other users) */}
          {user && user._id !== id && (
            <button
              onClick={toggleFollow}
              className="btn"
              style={{
                marginTop: "10px",
                background: amIFollowing ? "gray" : "#8b005d",
                color: "white",
              }}
            >
              {amIFollowing ? "Unfollow" : "Follow"}
            </button>
          )}
        </div>
      </div>

      {/* FOLLOWERS LIST */}
      <h3 style={{ marginTop: 25 }}>Followers ({followers.length})</h3>
      <ul>
        {followers.map((f) => (
          <li key={f._id}>{f.username}</li>
        ))}
      </ul>

      {/* FOLLOWING LIST */}
      <h3 style={{ marginTop: 25 }}>Following ({following.length})</h3>
      <ul>
        {following.map((f) => (
          <li key={f._id}>{f.username}</li>
        ))}
      </ul>

      {/* USER POSTS */}
      <h3 style={{ marginTop: 20 }}>Posts</h3>
      <div className="grid">
        {profile.posts?.map((p) => (
          <div key={p._id} className="card post-card">
            <img
              src={"http://localhost:5000" + p.imagePath}
              alt="post"
            />
          </div>
        ))}
      </div>

      {/* SAVED POSTS */}
      <h3 style={{ marginTop: 20 }}>Saved Posts</h3>
      <div className="grid">
        {saved.length === 0 && <p>No saved posts yet.</p>}

        {saved.map((p) => (
          <div key={p._id} className="card post-card">
            <img
              src={"http://localhost:5000" + p.imagePath}
              alt="saved"
              style={{ width: "100%", borderRadius: 12 }}
            />
          </div>
        ))}
      </div>
    </div>
  );
}
